const request = msg.payload;

const timestamp = Math.floor(Date.now() / 1000);
const timestampStr = timestamp.toString(16);
const timestampBuff = Buffer.from(timestamp.toString(16), 'hex');

const response = Buffer.alloc(23);
response.writeUInt8(0xA5, 0x00);  // msg begin

response.writeUInt8(0x0A, 0x01);    // msg length
response.writeUInt8(0x00, 0x02);

response.writeUInt8(0x10, 0x03);
response.writeUInt8(responseType(request.readUInt8(0x04)), 0x04);  // match req type

response.writeUInt8(request.readUInt8(0x05) >= 0xFF ? 0x00 : request.readUInt8(0x05) + 0x01, 0x05); // get from req + 1
response.writeUInt8(request.readUInt8(0x06), 0x06);  // get from req

response.writeUInt32LE(request.readUInt32LE(0x07), 0x07);  // wifi SN - get from req

response.writeUInt8(request.readUInt8(0x0B), 0x0B);  // get from req
response.writeUInt8(0x01, 0x0C);

response.writeUInt32LE(timestampBuff.readUInt32BE(0x00), 0x0D);  // timestamp

response.writeUInt8(0x78, 0x11);
response.writeUInt8(0x00, 0x12);
response.writeUInt8(0x00, 0x13);
response.writeUInt8(0x00, 0x14);

response.writeUInt8(calculateChecksum(response), 0x15);  //checksum
response.writeUInt8(0x15, 0x16);  // msg end

msg.payload = response;
msg.topic = 'local_server';
return msg;

function responseType(requestTypeByte) {
    // node.warn(`req code: 0x${requestTypeByte.toString(16)}`);
    let responseTypeByte = requestTypeByte - (0x30);
    responseTypeByte = Math.min(Math.max(responseTypeByte, 0x00), 0xFF); //min 0x00 max 255
    // switch(requestTypeByte) {
    //     case 0x41:
    //         responseTypeByte = 0x11;
    //         break;
    //     case 0x42:
    //         responseTypeByte = 0x12;
    //         break;
    //     case 0x43:
    //         responseTypeByte = 0x13;
    //         break;
    //     case 0x47:
    //         responseTypeByte = 0x17;
    //         break;
    //     case 0x48:
    //         responseTypeByte = 0x18;
    //         break;
    //     default:
    //         responseTypeByte = 0xFF;
    // }
    // node.warn(`res code: 0x${responseTypeByte.toString(16)}`);
    return responseTypeByte;
}
function calculateChecksum(message) {
    message = message.slice(1, -2);
    let sum = 0x0;
    for (const key of message.keys()) {
        sum += message[key];
    }
    sum = sum % 256;
    // node.warn( sum.toString(16));
    return sum;
}

