function readMacAddress(buffer) {
    let macAddressStr = '';
    for (let buff of buffer) {
        // node.warn(buff.toString(16));
        macAddressStr += `${`0${(buff.toString(16))}`.slice(-2)}:`;
    }
    return macAddressStr.substring(0, 17);
}
function readSensorTypeList(buffer) {
    let SensorTypeListStr = `${`0${(buffer[1].toString(16))}`.slice(-2)}`;
    SensorTypeListStr += `${`0${(buffer[0].toString(16))}`.slice(-2)}`;
    return SensorTypeListStr;
}

function decodeInverterStatus(status) {
    switch (status) {
        case 0x00:
            return `standby`;
        case 0x02:
            return `normal`;
        case 0x03:
            return `fault`;
        case 0x04:
            return `permanent`;
        default:
            return `0x${`0${(status.toString(16))}`.slice(-2)}`;
    }
}

